let{list:list}=require("postcss");module.exports.error=function(e){let t=new Error(e);throw t.autoprefixer=!0,t},module.exports.uniq=function(e){return[...new Set(e)]},module.exports.removeNote=function(e){return e.includes(" ")?e.split(" ")[0]:e},module.exports.escapeRegexp=function(e){return e.replace(/[$()*+-.?[\\\]^{|}]/g,"\\$&")},module.exports.regexp=function(e,t=!0){return t&&(e=this.escapeRegexp(e)),new RegExp(`(^|[\\s,(])(${e}($|[\\s(,]))`,"gi")},module.exports.editList=function(e,t){let r=list.comma(e),o=t(r,[]);if(r===o)return e;let n=e.match(/,\s*/);return n=n?n[0]:", ",o.join(n)},module.exports.splitSelector=function(e){return list.comma(e).map(e=>list.space(e).map(e=>e.split(/(?=\.|#)/g)))},module.exports.isPureNumber=function(e){return"number"==typeof e||"string"==typeof e&&/^[0-9]+$/.test(e)};