import log from"./log";export function validateConfig(i){0===i.content.files.length&&log.warn("content-problems",["The `content` option in your Tailwind CSS configuration is missing or empty.","Configure your content sources or your generated CSS will be missing styles.","https://tailwindcss.com/docs/content-configuration"]);try{let n=require("@tailwindcss/line-clamp");i.plugins.includes(n)&&(log.warn("line-clamp-in-core",["As of Tailwind CSS v3.3, the `@tailwindcss/line-clamp` plugin is now included by default.","Remove it from the `plugins` array in your configuration to eliminate this warning."]),i.plugins=i.plugins.filter(i=>i!==n))}catch{}return i}