class LineCounter{constructor(){this.lineStarts=[],this.addNewLine=t=>this.lineStarts.push(t),this.linePos=t=>{let i=0,e=this.lineStarts.length;for(;i<e;){const n=i+e>>1;this.lineStarts[n]<t?i=n+1:e=n}if(this.lineStarts[i]===t)return{line:i+1,col:1};if(0===i)return{line:0,col:t};return{line:i,col:t-this.lineStarts[i-1]+1}}}}export{LineCounter};