class LineCounter{constructor(){this.lineStarts=[],this.addNewLine=t=>this.lineStarts.push(t),this.linePos=t=>{let i=0,e=this.lineStarts.length;for(;i<e;){const s=i+e>>1;this.lineStarts[s]<t?i=s+1:e=s}return this.lineStarts[i]===t?{line:i+1,col:1}:0===i?{line:0,col:t}:{line:i,col:t-this.lineStarts[i-1]+1}}}}export{LineCounter};