const stringifyComment=n=>n.replace(/^(?!$)(?: $)?/gm,"#");function indentComment(n,e){return/^\n+$/.test(n)?n.substring(1):e?n.replace(/^(?! *$)/gm,e):n}const lineComment=(n,e,t)=>n.endsWith("\n")?indentComment(t,e):t.includes("\n")?"\n"+indentComment(t,e):(n.endsWith(" ")?"":" ")+t;export{indentComment,lineComment,stringifyComment};